import React, { Component } from 'react';
import PropTypes from 'prop-types';
import './index.scss'

class index extends Component {
    render () {

        return (
            <div className='steps'>
                <div className="stepWrap">
                    <div className={`step step1 ${this.isActive(1) ? 'active' : ''} `}>
                        1
                    </div>
                    <div className={`step step2  ${this.isActive(2) ? 'active' : ''}`}>
                        2
                    </div>
                    <div className={`step step3  ${this.isActive(3) ? 'active' : ''}`}>
                        3
                    </div>
                    <div className={`step step4  ${this.isActive(4) ? 'active' : ''}`}>
                        4
                    </div>
                </div>
                <div className="stepTitle">
                    <div className={`title 
                     ${this.isActive(1) ? 'active' : ''}
                    `}>
                        申办须知
                    </div>
                    <div className={`title 
                     ${this.isActive(2) ? 'active' : ''}
                    `}>
                        填写表单
                    </div>
                    <div className={`title 
                     ${this.isActive(3) ? 'active' : ''}
                    `}>
                        上传材料
                    </div>
                    <div className={`title 
                     ${this.isActive(4) ? 'active' : ''}
                    `}>
                        核对信息
                    </div>
                </div>
            </div>
        );
    }

    isActive (n) {
        let { activeStep } = this.props
        return activeStep === n || activeStep > n
    }
}


index.propTypes = {
    activeStep: PropTypes.number
};

index.defaultProps = {
    activeStep: 3
}


export default index;
