import * as React from 'react';
import { Button, Input } from 'antd-mobile';

import flv from 'flv.js';

const { useState, useEffect } = React;

// interface LiveDemoProps {
//     defaultUrl?: string,
//     onUrlChange?: Function
// }

export default function TestVodeo ({ defaultUrl = 'http://192.168.1.115:7001/live/movie.flv', onUrlChange }) {

    let player = null;
    let playerDom = null;

    const [liveUrl, setLiveUrl] = useState(defaultUrl);

    useEffect(() => {
        if (flv.isSupported) {
            console.log(1234)
            player = flv.createPlayer({
                type: 'flv',
                isLive: true,
                hasAudio: true,
                hasVideo: true,
                url: liveUrl,
                cors: true
            }, {
                enableWorker: false,
                lazyLoadMaxDuration: 3 * 60,
                seekType: 'range'
            });
            player.attachMediaElement(playerDom);
            player.load();

        } else {
            console.log('Your browser is not support flv.js');
        }
    }, []);

    function updatePlayer () {
        if (player) {
            player.unload();
            player.detachMediaElement();
            player.destroy();
            player = null;
        }

        player = flv.createPlayer({
            type: 'flv',
            isLive: true,
            hasAudio: false,
            hasVideo: true,
            url: liveUrl,
            cors: true
        }, {
            enableWorker: false,
            lazyLoadMaxDuration: 3 * 60,
            seekType: 'range'
        });
        player.attachMediaElement(playerDom);
        player.load();
    }

    return (
        <div className='live-demo'>
            <div className="modify-url">


                <input
                    value={liveUrl}
                    onChange={(value) => {
                        console.log(value)

                    }}
                />


                <button
                    type={'primary'}
                    onClick={() => {
                        updatePlayer();
                        onUrlChange && onUrlChange(liveUrl);
                    }}
                >修改</button>


            </div>
            <video
                style={{ width: '100%', height: '100%' }}
                controls
                className='video-demo'
                ref={(e) => playerDom = e}
            ></video>
        </div>
    );
}
